def fff(s):
    l = len(s)
    result = [float('inf')] * (l+1)
    result[0] = -1

    for t in range(1, l+1):
        for i in range(0, t):
            if palindrome(s[i:t]):
                result[t] = min(result[t], result[i]+1)
    print(result[l])
    return result[l]


def palindrome(s):
    return s == s[::-1]

